def read_tokens(filepath: str) -> list:
    file = open(filepath)
    stream = file.read()
    file.close()
    stream = [ord(char) for char in stream]
    tokens = afd.simulate_lexer(stream)

    f = open("./out/yapar_tokens.txt", "w")
    ws = '\n'
    for token in tokens:
        f.write(str(token))
        f.write(ws)
    f.close()
    print('-> Yapl Tokens founded written in ./out/yapar_tokens.txt')
    return tokens
